import React, {useState, useEffect, Fragment} from "react";
import axios from "axios";
import "./Menu.css"
import RestaurantIcon from '@mui/icons-material/Restaurant';
import {Link} from "react-router-dom";


const Menu = () => {
    const [page,setPage] = useState(1);
    const [total,setTotal] = useState(0)
    const [pages,setPages] = useState({curpage:1, totalpage:0,count:0,startPage:1,endPage:5})
    const [food,setFood] = useState([]);
    const [type,setType] = useState('');
    const [types,setTypes] = useState([]);

    useEffect(()=>{
        axios.get("http://localhost:8080/food/menu",{
            params:{
                page:pages.curpage,
                type:type
            }
        }).then(res=>{
            console.log(res.data)
            setFood(res.data)
            setPages((prevState) => {
                return{
                    ...prevState,
                    totalpage: res.data[0].totalpage,
                    count : res.data[0].count,
                    startPage: res.data[0].startPage,
                    endPage: res.data[0].endPage
                }
            })
        })
    },[type])
    useEffect(()=>{
        axios.get("http://localhost:8080/food/typelist")
            .then(res=>{
                console.log(res.data)
                setTypes(res.data)
            })
    },[])
    const typeChange = (type) => {
      setType(type.t)
    }
    const pagenation = () => {
      let tmp = [] ;
      for(let i = pages.startPage;i<=pages.endPage;i++){
          console.log(i)
          tmp.push(i);
      }
      const pageMap = tmp.map((i,index)=>(
          <li key={index} className={i===pages.curpage?'page-item active':'page-item'}><a className="page-link" href="#">{i}</a></li>
      ))
      return(
          <div style={{"margin":"0px auto"}}>
              <nav aria-label="...">
                  <ul className="pagination">
                      <li className={pages.startPage>1?'page-item':'page-item disabled'}>
                          <a className="page-link" href="#" tabIndex="-1" aria-disabled="true">이전</a>
                      </li>
                      {pageMap}
                      <li className={pages.endPage==pages.totalpage?'page-item disabled':'page-item'}>
                          <a className="page-link" href="#">다음</a>
                      </li>
                  </ul>
              </nav>
          </div>
      )
    }
    const fmap = food.map((f,index) => (
        <div key={index} className={"col-md-3"}>
            <div className="thumbnail">
                <Link to="#">
                    <img src={f.poster.substring(0,f.poster.indexOf("^"))} alt="Lights" style={{"width":"210px","height":"210px"}}/>
                    <div className="caption">
                        <p>{f.name}</p>
                    </div>
                </Link>
            </div>
        </div>
    ))
    const typeMap = types.map((t,index)=>(
        <button key={index} className={"tag-food"} onClick={()=>typeChange({t})}>{t}</button>
    ))
    return(
        <Fragment>
            <div className={"row"}>
                <h2>Menu List</h2>
                <div className={"tag-wrap"}>
                    {typeMap}
                </div>
            </div>
            <div className={"row"}>
                <h2 className={"menu_title"}><RestaurantIcon fontSize={"large"}/>&nbsp;메뉴별 맛집</h2>
            </div>
            <div className={"row"}>
                {fmap}
            </div>
                {pagenation}
        </Fragment>
    )
}

export default Menu
