/*global kakao */
import React, { useEffect } from "react";

const Map = (props) => {
    const { kakao } =window;
    useEffect(() => {
        mapscript();
    }, []);

    const mapscript = () => {
        let container = document.getElementById("map");
        let options = {
            center: new kakao.maps.LatLng(37.624915253753194, 127.15122688059974),
            level: 3,
        };
        const map = new kakao.maps.Map(container, options);
        //위도, 경도로 변환 및 마커표시
        var geocoder = new kakao.maps.services.Geocoder();
        geocoder.addressSearch((String(props.data.address).substring(0,String(props.data.address).lastIndexOf("지번"))), function (result, status) {

            if (status === kakao.maps.services.Status.OK) {

                var coords = new kakao.maps.LatLng(result[0].y, result[0].x);

                var marker = new kakao.maps.Marker({
                    map: map,
                    position: coords
                });
                var infowindow = new kakao.maps.InfoWindow({
                    content: '<div style="width:150px;text-align:center;padding:6px 0;color:black;border-radius: 10px">'+
                                props.data.name+
                            '</div>'
                });
                infowindow.open(map, marker);

                map.setCenter(coords);
            }
        });
    }
    return(
        <div id="map" style={{"width": "100%", "height": "400px"}}></div>
    )
}
export default Map
