import {Carousel} from "react-bootstrap";
import {useState, useEffect, Fragment} from "react";
import axios from "axios";

function ControlledCarousel() {
    const [index, setIndex] = useState(0);
    const [category,setCategory] = useState([])

    useEffect(()=>{
        console.log("hi")
        axios.get("http://localhost:8080/food/category")
            .then(res=>{
                console.log(res.data)
                setCategory(res.data)
            })
    },[])

    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };
    // const carousel_img = (index) =>{
    //     return(
    //         <Fragment>
    //             <img src={category[index].poster} alt={category[index].title}/>
    //             <div className={"carousel-name"}>
    //                 <span>
    //                     {category[index].title}
    //                 </span>
    //                 <p>
    //                     {category[index].subject}
    //                 </p>
    //             </div>
    //         </Fragment>
    //     )
    // }
    return (
        <Fragment>
            <div>
                <h2 className={"main-carousel-title"}>믿고보는 맛집 리스트</h2>
            </div>
            <Carousel variant={"dark"} >
                <Carousel.Item>
                    <div className={"carousel-warp"}>
                        <div className={"row"}>
                            <div className={"col-md-4"}>
                                <img src={category[1].poster} alt={category[1].title}/>
                                <div className={"carousel-name"}>
                                    <span>
                                        {category[1].title}
                                    </span>
                                    <p>
                                        {category[1].subject}
                                    </p>
                                </div>
                            </div>
                            <div className={"col-md-4"}>
                                <img src={category[2].poster}/>
                            </div>
                            <div className={"col-md-4"}>
                                <img src={category[3].poster}/>
                            </div>
                        </div>
                        <div className={"row"}>
                            <div className={"col-md-4"}>
                                <img src={category[4].poster}/>
                            </div>
                            <div className={"col-md-4"}>
                                <img src={category[5].poster}/>
                            </div>
                            <div className={"col-md-4"}>
                                <img src={category[6].poster}/>
                            </div>
                        </div>
                    </div>
                </Carousel.Item>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src="images/demo/slides/02.png"
                        alt="Second slide"
                    />

                    <Carousel.Caption>
                        <h3>Second slide label</h3>
                        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src="images/demo/slides/03.png"
                        alt="Third slide"
                    />
                    <Carousel.Caption>
                        <h3>Third slide label</h3>
                        <p>
                            Praesent commodo cursus magna, vel scelerisque nisl consectetur.
                        </p>
                    </Carousel.Caption>
                </Carousel.Item>
            </Carousel>
        </Fragment>
    );
}

export default ControlledCarousel
