import React, {useState, useEffect, Fragment} from "react";
import axios from "axios";
import "./Menu.css"
import RestaurantIcon from '@mui/icons-material/Restaurant';
import {Link} from "react-router-dom";


const Menu = () => {
    const [page,setPage] = useState(1);
    const [total,setTotal] = useState(0)
    const [pages,setPages] = useState({curpage:1, totalpage:0,count:0,startPage:1,endPage:5})
    const [food,setFood] = useState([]);
    const [type,setType] = useState('이탈리안');
    const [types,setTypes] = useState([]);

    // type이 변경시 작동 / 처음 실행시 작동
    useEffect(()=>{
        {getFoodList()}
    },[type])
    // foodlist 받아오기
    const getFoodList = () => {
        axios.get("http://localhost:8080/food/menu",{
            params:{
                page:pages.curpage,
                type:type
            }
        }).then(res=>{
            console.log(res.data)
            setFood(res.data)
            setPages((prevState) => {
                return{
                    ...prevState,
                    totalpage: res.data[0].totalpage,
                    count : res.data[0].count,
                    startPage: res.data[0].startPage,
                    endPage: res.data[0].endPage
                }
            })
        })
    }
    // typeList 받아오기
    useEffect(()=>{
        axios.get("http://localhost:8080/food/typelist")
            .then(res=>{
                console.log(res.data)
                setTypes(res.data)
            })
    },[])
    // type 버튼 클릭시 변경
    const typeChange = (type) => {
      setType(type.t)
    }
    // pagenation 구현
    const pagenation = () => {
      let tmp = [] ;
      for(let i = pages.startPage;i<=pages.endPage;i++) {
          tmp.push(i);
      }
      // 페이지 이동
      const changePage =  (p) => {
          console.log(p)
           setPages({
               ...pages,
               curpage: p
           })
          console.log(pages.curpage)
          {realPage()}
      }
      const realPage = () => {
          {getFoodList()}
      }
      let pageMap = tmp.map((i,index)=>(
          <li key={index} className={i===pages.curpage?'page-item active':'page-item'}>
              <span className="page-link" onClick={()=>{changePage(i)}}>{i}</span>
          </li>
      ))
      return(
          <div style={{"margin":"0px auto"}}>
              <nav aria-label="...">
                  <ul className="pagination">
                      <li className={pages.startPage>1?'page-item':'page-item disabled'}>
                          <Link className="page-link" to="#" tabIndex="-1" aria-disabled="true">이전</Link>
                      </li>
                      {pageMap}
                      <li className={pages.endPage>=pages.totalpage?'page-item disabled':'page-item'}>
                          <Link className="page-link" to="#">다음</Link>
                      </li>
                  </ul>
              </nav>
          </div>
      )
    }
    // foodlist 반복 출력 구현
    const fmap = food.map((f,index) => (
        <div key={index} className={"col-md-3"}>
            <div className="thumbnail">
                <Link to="#">
                    <img src={f.poster.substring(0,f.poster.indexOf("^"))} alt="Lights" style={{"width":"210px","height":"210px"}}/>
                    <div className="caption">
                        <p>{f.name}</p>
                    </div>
                </Link>
            </div>
        </div>
    ))
    // typelist 반복 출력 구현
    const typeMap = types.map((t,index)=>(
        <button key={index} className={"tag-food"} onClick={()=>typeChange({t})}>{t}</button>
    ))
    return(
        <Fragment>
            <div className={"row"}>
                <h2>Menu List</h2>
                <div className={"tag-wrap"}>
                    {typeMap}
                </div>
            </div>
            <div className={"row"}>
                <h2 className={"menu_title"}><RestaurantIcon fontSize={"large"}/>&nbsp;메뉴별 맛집</h2>
            </div>
            <div className={"row"}>
                {fmap}
            </div>
                {pagenation()}
        </Fragment>
    )
}

export default Menu
