import React from 'react';
import './Modal.css';
import axios from "axios";

const Modal = (props) => {
    // 열기, 닫기, 모달 헤더 텍스트를 부모로부터 받아옴
    const { open, close, no } = props;
    console.log(no)
    if(no.no!=''){
        return (
            // 모달이 열릴때 openModal 클래스가 생성된다.
            <div className={open ? 'openModal modal' : 'modal'}>
                {open ? (
                    <section>
                        <header>
                            {no.title}
                            <button className="close" onClick={close}>
                                &times;
                            </button>
                        </header>
                        <main>{no.msg}</main>
                        <footer>
                            <button className="close" onClick={close}>
                                close
                            </button>
                        </footer>
                    </section>
                ) : null}
            </div>
        );
    }else{
        return null
    }

};
export default Modal
