const vallidation = (values) => {
    let errors={};
    if(!values.fullname){
       errors.fullname="이름을 입력하시오!"
    }
    if(!values.email){
        errors.email="이메일을 입력하시오!"
    }else if(!/\S+@\S+\.\S+/.test(values.email)){
        errors.email="이메일 형식으로 작성하시오!"
    }
    if (!values.password){
        errors.passowrd="비밀번호를 입력하시오!"
    }else if (values.password.length<5){
        errors.passowrd="비밀번호는 5자리 이상 입력하시오!"
    }

    return errors;
}
export default vallidation()
