import React,{useState,useEffect} from "react";
import axios from "axios";


const Menu = () => {
    const [page,setPage] = useState(1);
    const [food,setFood] = useState([]);
    const [type,setType] = useState('고기');

    useEffect(()=>{
        axios.post("http://localhost:8080/food/menu",{
                page:{page},
                type:{type}
        }).then(res=>{
            console.log(res.data)
        })
    },[])
    return(
        <div>
            {food.map(f => (
                <div className="col-md-3">
                    <div className="thumbnail">
                        <a href="#">
                            <img src={f.poster.substring(0,f.poster.indexOf("^"))} alt="Lights" style={{"width":"100%"}}/>
                            <div className="caption">
                                <p style={{"fontSize":"8px"}}>{f.name}</p>
                            </div>
                        </a>
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Menu
